using System;\nusing System.IO;\n\nclass BitWriter\n{\n    public static void WriteBits(string fileName, BitArray bits)\n    {\n        using (BinaryWriter writer = new BinaryWriter(File.Open(fileName, FileMode.Create)))\n        {\n            for (int i = 0; i < bits.Length; i++)\n            {\n                writer.Write(bits[i] ? (byte)1 : (byte)0);\n            }\n        }\n    }\n}