using Excel = Microsoft.Office.Interop.Excel;\n\n// Create a new Excel application instance\nExcel.Application excelApp = new Excel.Application();\n\n// Open the workbook\nExcel.Workbook workbook = excelApp.Workbooks.Open(@"C:\Path\To\Your\Workbook.xlsx");\n\n// Add a new worksheet to the workbook\nExcel.Worksheet newWorksheet = (Excel.Worksheet)workbook.Worksheets.Add(\n    Type.Missing, Type.Missing, Type.Missing, Type.Missing);\n\n// Set the name of the new worksheet\nnewWorksheet.Name = "New Worksheet";\n\n// Save the workbook\nworkbook.Save();\n\n// Close the workbook and quit the Excel application\nworkbook.Close();\nexcelApp.Quit();