protected void Page_Init(object sender, EventArgs e)\n{\n    string[] controlsToValidate = ConfigurationManager.AppSettings["ControlsToValidateRequiredFields"].Split(',');\n\n    foreach (string controlName in controlsToValidate)\n    {\n        Control control = Page.FindControl(controlName);\n        if (control != null)\n        {\n            RequiredFieldValidator validator = new RequiredFieldValidator();\n            validator.ControlToValidate = control.ID;\n            validator.ErrorMessage = "Field is required";\n            validator.Display = ValidatorDisplay.Dynamic;\n            Page.Controls.Add(validator);\n        }\n    }\n}