[Authorize]\npublic ActionResult Delete(int commentId)\n{\n    var comment = _dbContext.Comments.Find(commentId);\n    if (comment == null || !UserHasPermissionToDeleteComment(comment))\n    {\n        return HttpNotFound();\n    }\n    _dbContext.Comments.Remove(comment);\n    _dbContext.SaveChanges();\n    return RedirectToAction("Index");\n}\n\nprivate bool UserHasPermissionToDeleteComment(Comment comment)\n{\n    return comment.AuthorId == User.Identity.GetUserId() || User.IsInRole("Admin");\n}