using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Data.Odbc;\n\n// ...\n\nstring sqlServerConnStr = ConfigurationManager.ConnectionStrings["SQLServer"].ToString();\nstring oldServerConnStr = ConfigurationManager.ConnectionStrings["Server"].ToString();\n\n// Create connections\nusing (SqlConnection sqlConn = new SqlConnection(sqlServerConnStr))\nusing (OdbcConnection oldConn = new OdbcConnection(oldServerConnStr))\n{\n    // Delete existing data from SQL Server\n    string deleteStatement = "TRUNCATE TABLE Data"; // Use TRUNCATE instead of DELETE for performance\n    using (SqlCommand deleteComm = new SqlCommand(deleteStatement, sqlConn))\n    {\n        sqlConn.Open();\n        deleteComm.ExecuteNonQuery();\n        sqlConn.Close();\n    }\n\n    // Retrieve data from old server\n    string selectStatement = "SELECT * FROM BILL.TRANSACTIONS WHERE TRANSACTION = 'NEW'";\n    using (OdbcCommand selectComm = new OdbcCommand(selectStatement, oldConn))\n    {\n        oldConn.Open();\n        using (OdbcDataReader reader = selectComm.ExecuteReader())\n        {\n            // Insert data into SQL Server\n            string insertStatement = "INSERT INTO Data (column1, column2, ...) VALUES (@column1, @column2, ...)";\n            using (SqlCommand insertComm = new SqlCommand(insertStatement, sqlConn))\n            {\n                sqlConn.Open();\n                while (reader.Read())\n                {\n                    // Map old server columns to SQL Server columns\n                    insertComm.Parameters.AddWithValue("@column1", reader["column1"].ToString());\n                    insertComm.Parameters.AddWithValue("@column2", reader["column2"].ToString());\n                    // ...\n\n                    insertComm.ExecuteNonQuery();\n                }\n                sqlConn.Close();\n            }\n        }\n        oldConn.Close();\n    }\n}