using SQLite;\n\npublic class MyTable\n{\n    [PrimaryKey, AutoIncrement]\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class MyRepository\n{\n    private readonly SQLiteConnection _connection;\n\n    public MyRepository(string dbPath)\n    {\n        _connection = new SQLiteConnection(dbPath);\n        _connection.CreateTable<MyTable>();\n    }\n\n    public void Insert(MyTable item)\n    {\n        _connection.Insert(item);\n    }\n\n    public MyTable GetById(int id)\n    {\n        return _connection.Get<MyTable>(id);\n    }\n\n    public void Update(MyTable item)\n    {\n        _connection.Update(item);\n    }\n\n    public void Delete(int id)\n    {\n        _connection.Delete<MyTable>(id);\n    }\n}