using (SqlConnection sqlConn = new SqlConnection("SQL Server connection string"))\n{\n    sqlConn.Open();\n\n    // Query SQL Server database\n    SqlCommand sqlCommand = new SqlCommand("SELECT * FROM materials", sqlConn);\n    SqlDataReader sqlReader = sqlCommand.ExecuteReader();\n\n    // Load Access database into a DataTable\n    using (OleDbConnection accessConn = new OleDbConnection("Access connection string"))\n    {\n        accessConn.Open();\n        OleDbCommand accessCommand = new OleDbCommand("SELECT * FROM materials", accessConn);\n        OleDbDataReader accessReader = accessCommand.ExecuteReader();\n        DataTable accessTable = new DataTable();\n        accessTable.Load(accessReader);\n    }\n\n    // Perform join or filtering between the two datasets\n    // ...\n}