using System.Windows;\nusing System.Windows.Controls;\n\nnamespace SilverlightControl\n{\n    public partial class MySilverlightControl : UserControl\n    {\n        public string Xaml { get; set; }\n\n        public MySilverlightControl()\n        {\n            InitializeComponent();\n        }\n\n        public void SetXaml(string xaml)\n        {\n            Xaml = xaml;\n            // Parse the XAML and add it to the control\n            var xamlReader = new XamlReader();\n            var element = xamlReader.Load(xaml);\n            this.Content = element;\n        }\n    }\n}