using NowSMSLib;\n\nclass SendMMS\n{\n    public static void Main()\n    {\n        // Set up the NowSMS gateway\n        string gatewayAddress = "your_gateway_address";\n        int gatewayPort = 8800;\n        string username = "your_username";\n        string password = "your_password";\n\n        // Create an instance of the NowSMS gateway\n        NowSMSGateway gateway = new NowSMSGateway(gatewayAddress, gatewayPort, username, password);\n\n        // Set up the MMS message\n        string recipientNumber = "+1234567890";\n        string subject = "Hello from C#!";\n        string messageText = "This is a test MMS message sent from C#.";\n        byte[] imageBytes = File.ReadAllBytes("path_to_image.jpg");\n\n        // Create the MMS message\n        MMSMessage message = new MMSMessage(subject, messageText, imageBytes);\n\n        // Send the MMS message\n        gateway.SendMessage(recipientNumber, message);\n    }\n}