using System;\nusing System.IO;\nusing NFS.NET;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Create an NFS client instance\n        var client = new NFSClient("nfs.example.com", 2049);\n\n        // Connect to the NFS server\n        client.Connect();\n\n        // List the files in the root directory\n        var files = client.ListFiles("/");\n        foreach (var file in files)\n        {\n            Console.WriteLine(file.Name);\n        }\n\n        // Disconnect from the NFS server\n        client.Disconnect();\n    }\n}