using System;\nusing System.Text;\n\npublic class UnicodeToAsciiConverter\n{\n    public static string ConvertUnicodeToAscii(string unicodeString)\n    {\n        // Define the mapping from Unicode to extended ASCII\n        var unicodeToAsciiMap = new Dictionary<char, char>\n        {\n            { '\u02C6', '^' }, // MODIFIER LETTER CIRCUMFLEX ACCENT\n            // Add more mappings as needed\n        };\n\n        var asciiStringBuilder = new StringBuilder();\n\n        foreach (var c in unicodeString)\n        {\n            if (unicodeToAsciiMap.TryGetValue(c, out var asciiChar))\n            {\n                asciiStringBuilder.Append(asciiChar);\n            }\n            else\n            {\n                // If no mapping is found, append the original character\n                asciiStringBuilder.Append(c);\n            }\n        }\n\n        return asciiStringBuilder.ToString();\n    }\n\n    public static void Main(string[] args)\n    {\n        var unicodeString = "\u02C6e"; // MODIFIER LETTER CIRCUMFLEX ACCENT + LATIN SMALL LETTER E\n        var asciiString = ConvertUnicodeToAscii(unicodeString);\n\n        Console.WriteLine($"Unicode: {unicodeString}");\n        Console.WriteLine($"ASCII: {asciiString}");\n    }\n}