private ISessionFactory CreateSessionFactory()\n{\n    return Fluently.Configure()\n        .Database(MsSqlConfiguration.MsSql2005\n            .ConnectionString(c => c.Is(@"Data Source=foo;Initial Catalog=bar;Integrated Security=True")))\n        .Mappings(m => m.AutoMappings.Add(\n            AutoPersistenceModel.MapEntitiesFromAssemblyOf<Employee>()\n                .Where(t => t.Namespace.Contains("Entities"))\n                .ConventionDiscovery.GetConventionsFromAssembly(Assembly.GetExecutingAssembly())))\n        .ExposeConfiguration(BuildSchema)\n        .BuildSessionFactory();\n}\n\nprivate void BuildSchema(Configuration cfg)\n{\n    new SchemaExport(cfg).Create(false, true);\n}