using Microsoft.WindowsMobile.Status;\n\npublic class GPRSController\n{\n    public void PowerOnGPRS()\n    {\n        var gprsAdapter = new SystemState(SystemProperty.ConnectionsGprs);\n        if (gprsAdapter.State == false)\n        {\n            SystemState.SetSystemState(SystemProperty.ConnectionsGprs, true);\n        }\n    }\n\n    public void PowerOffGPRS()\n    {\n        var gprsAdapter = new SystemState(SystemProperty.ConnectionsGprs);\n        if (gprsAdapter.State == true)\n        {\n            SystemState.SetSystemState(SystemProperty.ConnectionsGprs, false);\n        }\n    }\n}