using System;\nusing System.Data.SqlClient;\nusing System.IO;\n\npublic class SqlScriptImporter\n{\n    public void ImportScript(string connectionString, string scriptPath)\n    {\n        using (var connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            using (var command = new SqlCommand())\n            {\n                command.Connection = connection;\n\n                using (var reader = new StreamReader(scriptPath))\n                {\n                    string script = reader.ReadToEnd();\n\n                    // Split the script into individual statements\n                    string[] statements = script.Split(new[] { "GO" }, StringSplitOptions.RemoveEmptyEntries);\n\n                    foreach (string statement in statements)\n                    {\n                        command.CommandText = statement.Trim();\n                        command.ExecuteNonQuery();\n                    }\n                }\n            }\n        }\n    }\n}