using System.Data.OleDb;\n\n// Connect to the MS Access database\nstring connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=your_database.mdb";\nOleDbConnection connection = new OleDbConnection(connectionString);\nconnection.Open();\n\n// Query the MSysDataTypes system table\nstring query = "SELECT TypeName FROM MSysDataTypes";\nOleDbCommand command = new OleDbCommand(query, connection);\nOleDbDataReader reader = command.ExecuteReader();\n\n// Populate the combobox\nwhile (reader.Read())\n{\n    string dataType = reader["TypeName"].ToString();\n    comboBox1.Items.Add(dataType);\n}\n\n// Clean up\nreader.Close();\nconnection.Close();