// Assume 'MyBizObject' is your business object and 'Save' is the method that triggers the save event\n\n// Unhook the ErrorProvider from the biz object initially\nMe.ErrorProvider1.DataSource = null;\n\n// In the Save method of your biz object, manually validate and display errors\nMyBizObject.Save += (sender, args) => {\n    // Validate the biz object manually\n    var errors = MyBizObject.GetErrors();\n    if (errors.Count > 0) {\n        // Display the errors using the ErrorProvider\n        Me.ErrorProvider1.DataSource = MyBizObject;\n        Me.ErrorProvider1.DataBind();\n    } else {\n        // Save the biz object if no errors\n        // ...\n    }\n};