// load the embedded DLL at runtime\nusing System.Runtime.InteropServices;\n\nclass MyWrapper\n{\n    [DllImport("kernel32")]\n    private static extern IntPtr LoadLibrary(byte[] dllData);\n\n    private IntPtr LoadEmbeddedDll()\n    {\n        var assembly = Assembly.GetExecutingAssembly();\n        var resourceName = "mycppdll.dll";\n\n        using (var stream = assembly.GetManifestResourceStream(resourceName))\n        {\n            var dllData = new byte[stream.Length];\n            stream.Read(dllData, 0, dllData.Length);\n            return LoadLibrary(dllData);\n        }\n    }\n}