using System;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program {\n    static void Main() {\n        X509Store store = new X509Store("MY", StoreLocation.LocalMachine);\n        store.Open(OpenFlags.ReadOnly);\n\n        X509Certificate2 cert = store.Certificates.Find(X509FindType.FindBySubjectName, "your_subject_name", false)[0];\n\n        string fileName = cert.PrivateKey.CspKeyContainerInfo.UniqueKeyContainerName;\n\n        // Now you have the file name in fileName\n\n        store.Close();\n    }\n}