using System.Security.Cryptography;\nusing System.Text;\n\n// Generate a GUID\nGuid userId = Guid.NewGuid();\n\n// Hash the GUID using SHA-256\nbyte[] hashedBytes;\nusing (var sha256 = SHA256.Create())\n{\n    hashedBytes = sha256.ComputeHash(Encoding.UTF8.GetBytes(userId.ToString()));\n}\n\n// Convert the hashed bytes to a hexadecimal string\nstring hashedId = BitConverter.ToString(hashedBytes).Replace("-", "").ToLower();\n\n// Use the hashed ID in your URL\nstring url = $"https://example.com/profile/{hashedId}";