public class CentralAuthClientCredentials : ClientCredentials\n{\n    public CentralAuthClientCredentials()\n    {\n        var clientCertificateElement = (X509InitiatorCertificateClientElement)ConfigurationManager.GetSection("clientCertificate");\n        var serviceCertificateElement = (X509RecipientCertificateClientElement)ConfigurationManager.GetSection("serviceCertificate");\n\n        this.ClientCertificate.SetCertificate(\n            StoreLocation.CurrentUser,\n            StoreName.My,\n            X509FindType.FindBySubjectName,\n            clientCertificateElement.FindValue\n        );\n\n        this.ServiceCertificate.SetDefaultCertificate(\n            StoreLocation.CurrentUser,\n            StoreName.My,\n            X509FindType.FindBySubjectName,\n            serviceCertificateElement.FindValue\n        );\n    }\n}