using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class TripleDESKeyConverter\n{\n    public static byte[] ConvertCryptoAPIKeyBlobToNETKey(byte[] keyBlob)\n    {\n        // Extract the key bytes from the blob\n        byte[] keyBytes = new byte[24]; // 168-bit key\n        Array.Copy(keyBlob, 8, keyBytes, 0, 24); // skip the first 8 bytes (header)\n\n        // Add parity bits to convert to 192-bit key\n        byte[] netKeyBytes = new byte[32]; // 192-bit key\n        for (int i = 0; i < 24; i += 8)\n        {\n            byte[] keyPart = new byte[8];\n            Array.Copy(keyBytes, i, keyPart, 0, 8);\n            byte[] parityKeyPart = AddParityBits(keyPart);\n            Array.Copy(parityKeyPart, 0, netKeyBytes, i, 8);\n        }\n\n        return netKeyBytes;\n    }\n\n    private static byte[] AddParityBits(byte[] keyPart)\n    {\n        byte[] parityKeyPart = new byte[8];\n        for (int i = 0; i < 8; i++)\n        {\n            parityKeyPart[i] = (byte)((keyPart[i] << 1) | (keyPart[i] & 0x01));\n        }\n        return parityKeyPart;\n    }\n}