using OpenQA.Selenium;\nusing OpenQA.Selenium.Remote;\n\nclass WinFormsTest\n{\n    [Test]\n    public void TestLoginButton()\n    {\n        // Create a new instance of the Chrome driver\n        IWebDriver driver = new RemoteWebDriver(new Uri("http://localhost:9515"), DesiredCapabilities.Chrome());\n\n        // Launch the WinForms application\n        driver.Navigate().GoToUrl("file:///C:/Path/To/Your/WinForms/Application.exe");\n\n        // Find the login button\n        IWebElement loginButton = driver.FindElement(By.Name("btnLogin"));\n\n        // Click the login button\n        loginButton.Click();\n\n        // Verify the login result\n        Assert.IsTrue(driver.FindElement(By.Name("lblLoginResult")).Text == "Login successful");\n\n        // Close the application\n        driver.Quit();\n    }\n}