using System;\nusing System.Management.Automation;\nusing System.Management.Automation.Runspaces;\n\npublic class RemoteFileReader\n{\n    public static string ReadRemoteFile(string remoteMachineName, string filePath)\n    {\n        // Create a new PowerShell runspace\n        Runspace runspace = RunspaceFactory.CreateRunspace();\n        runspace.Open();\n\n        // Create a new PowerShell command\n        PowerShell command = PowerShell.Create();\n        command.Runspace = runspace;\n\n        // Use Invoke-Command to execute the Get-Content cmdlet on the remote machine\n        command.AddCommand("Invoke-Command");\n        command.AddParameter("ComputerName", remoteMachineName);\n        command.AddParameter("ScriptBlock", ScriptBlock.Create("Get-Content -Path '" + filePath + "' -Encoding UTF8"));\n\n        // Execute the command and get the result\n        Collection<PSObject> result = command.Invoke();\n\n        // Close the runspace\n        runspace.Close();\n\n        // Return the file contents\n        if (result.Count > 0)\n        {\n            return result[0].ToString();\n        }\n        else\n        {\n            throw new Exception("Failed to read the remote file.");\n        }\n    }\n}