ApplicationType appType = ApplicationTypeDetector.GetApplicationType();\nswitch (appType)\n{\n    case ApplicationType.Console:\n        Console.WriteLine("Running in a console application");\n        break;\n    case ApplicationType.WinForms:\n        Console.WriteLine("Running in a Windows Forms application");\n        break;\n    case ApplicationType.AspNet:\n        Console.WriteLine("Running in an ASP.NET application");\n        break;\n    case ApplicationType.WindowsService:\n        Console.WriteLine("Running in a Windows Service");\n        break;\n    default:\n        Console.WriteLine("Unknown application type");\n        break;\n}