using System;\nusing System.Data.SqlClient;\nusing System.Xml;\n\npublic class XmlToSql\n{\n    public void InsertXmlData(string xmlFile, string connectionString)\n    {\n        // Load the XML file\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load(xmlFile);\n\n        // Create a connection to the database\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            // Iterate through the XML nodes and extract the data\n            foreach (XmlNode node in xmlDoc.SelectNodes("/root/node"))\n            {\n                string name = node.SelectSingleNode("name").InnerText;\n                string value = node.SelectSingleNode("value").InnerText;\n\n                // Use a parameterized query to insert the data\n                using (SqlCommand command = new SqlCommand("INSERT INTO mytable (name, value) VALUES (@name, @value)", connection))\n                {\n                    command.Parameters.AddWithValue("@name", name);\n                    command.Parameters.AddWithValue("@value", value);\n\n                    command.ExecuteNonQuery();\n                }\n            }\n        }\n    }\n}