using System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a list of integers\n        List<int> numbers = new List<int>();\n\n        // Add elements to the list\n        numbers.Add(1);\n        numbers.Add(2);\n        numbers.Add(3);\n\n        // Iterate over the list\n        foreach (int number in numbers)\n        {\n            Console.WriteLine(number);\n        }\n    }\n}