using Microsoft.CodeAnalysis;\nusing Microsoft.CodeAnalysis.CSharp;\nusing Microsoft.CodeAnalysis.Emit;\n\n// Create a C# script\nstring script = "using System; class Test { public static void Main() { Console.WriteLine(\"Hello\"); } }";\n\n// Parse the script\nSyntaxTree tree = CSharpSyntaxTree.ParseText(script);\n\n// Compile the script\nCSharpCompilation compilation = new CSharpCompilation(new[] { tree });\nusing (var ms = new MemoryStream())\n{\n    EmitResult result = compilation.Emit(ms);\n    if (result.Success)\n    {\n        // Execute the script\n        ms.Position = 0;\n        Assembly assembly = Assembly.Load(ms.ToArray());\n        Type type = assembly.GetType("Test");\n        MethodInfo method = type.GetMethod("Main");\n        method.Invoke(null, null);\n    }\n}