protected void ManagerList_GV_RowCommand(object sender, GridViewCommandEventArgs e)\n{\n    if (e.CommandName == "deleteRow")\n    {\n        LinkButton deleteButton = (LinkButton)e.CommandSource;\n        GridViewRow row = (GridViewRow)deleteButton.Parent.Parent;\n        int managerID = (int)ManagerList_GV.DataKeys[row.RowIndex].Value;\n\n        // Call your business layer subroutine to delete the manager\n        DeleteManager(managerID);\n    }\n}