[TestFixture]\npublic class File AccessTests\n{\n    [Test]\n    public void MultipleThreadFileAccess()\n    {\n        // Create a file to test with\n        string filePath = Path.GetTempFileName();\n        File.WriteAllText(filePath, "Test data");\n\n        // Define the number of threads to use\n        int numThreads = 10;\n\n        // Create a list to store the thread results\n        List<bool> results = new List<bool>();\n\n        // Create and start the threads\n        for (int i = 0; i < numThreads; i++)\n        {\n            Thread thread = new Thread(() =>\n            {\n                try\n                {\n                    // Call the file access method\n                    File.ReadAllText(filePath);\n                    results.Add(true);\n                }\n                catch (Exception ex)\n                {\n                    results.Add(false);\n                }\n            });\n            thread.Start();\n        }\n\n        // Wait for all threads to complete\n        foreach (Thread thread in Process.GetCurrentProcess().Threads)\n        {\n            thread.Join();\n        }\n\n        // Check the results\n        Assert.IsTrue(results.All(result => result));\n    }\n}