[TestFixture]\npublic class FileAccessTests\n{\n    [Test]\n    public void MultipleThreadFileAccess()\n    {\n        // Create a file to test with\n        string filePath = Path.GetTempFileName();\n        File.WriteAllText(filePath, "Test data");\n\n        // Define the number of threads to use\n        int numThreads = 10;\n\n        // Create a list to store the thread results\n        List<bool> results = new List<bool>();\n\n        // Use Parallel.Invoke to run the threads\n        Parallel.Invoke(() =>\n        {\n            for (int i = 0; i < numThreads; i++)\n            {\n                try\n                {\n                    // Call the file access method\n                    File.ReadAllText(filePath);\n                    results.Add(true);\n                }\n                catch (Exception ex)\n                {\n                    results.Add(false);\n                }\n            }\n        });\n\n        // Check the results\n        Assert.IsTrue(results.All(result => result));\n    }\n}