public sealed class Singleton\n{\n    private static Singleton _instance;\n    private static readonly object _lock = new object();\n\n    private Singleton() { }\n\n    public static Singleton Instance\n    {\n        get\n        {\n            if (_instance == null)\n            {\n                lock (_lock)\n                {\n                    if (_instance == null)\n                    {\n                        _instance = new Singleton();\n                    }\n                }\n            }\n            return _instance;\n        }\n    }\n}