using System;\nusing System.Xml;\n\nnamespace DeepZoomApi\n{\n    public class DzcOutputXmlGenerator\n    {\n        public string GenerateDzcOutputXml(string imageUri, int width, int height)\n        {\n            XmlDocument doc = new XmlDocument();\n            XmlElement root = doc.CreateElement("Image");\n            doc.AppendChild(root);\n\n            XmlElement size = doc.CreateElement("Size");\n            size.SetAttribute("Width", width.ToString());\n            size.SetAttribute("Height", height.ToString());\n            root.AppendChild(size);\n\n            XmlElement displayRect = doc.CreateElement("DisplayRect");\n            displayRect.SetAttribute("X", "0");\n            displayRect.SetAttribute("Y", "0");\n            displayRect.SetAttribute("Width", width.ToString());\n            displayRect.SetAttribute("Height", height.ToString());\n            root.AppendChild(displayRect);\n\n            return doc.OuterXml;\n        }\n    }\n}