using System;\nusing System.Net.Http;\nusing System.Text;\nusing Newtonsoft.Json;\n\nnamespace PhoneValidator\n{\n    public class PhoneNumberValidator\n    {\n        private const string TwilioAccountSid = "your_account_sid";\n        private const string TwilioAuthToken = "your_auth_token";\n\n        public bool ValidatePhoneNumber(string phoneNumber)\n        {\n            var client = new HttpClient();\n            client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Basic", Convert.ToBase64String(Encoding.ASCII.GetBytes($"{TwilioAccountSid}:{TwilioAuthToken}")));\n\n            var response = client.GetAsync($"https://lookups.twilio.com/v1/PhoneNumbers/{phoneNumber}?Type=carrier").Result;\n\n            if (response.IsSuccessStatusCode)\n            {\n                var responseData = JsonConvert.DeserializeObject<TwilioResponse>(response.Content.ReadAsStringAsync().Result);\n                return responseData.carrier != null && responseData.carrier.name != null;\n            }\n\n            return false;\n        }\n    }\n\n    public class TwilioResponse\n    {\n        public Carrier carrier { get; set; }\n    }\n\n    public class Carrier\n    {\n        public string name { get; set; }\n    }\n}