using Microsoft.SharePoint;\nusing Microsoft.SharePoint.WebControls;\nusing System.Collections.Generic;\n\npublic class CustomGridView : SPGridView\n{\n    protected override void RenderContents(HtmlTextWriter writer)\n    {\n        // Get your data from the external service\n        List<YourObjectType> data = YourWebService.GetYourData();\n\n        // Define the columns for the grid\n        this.Columns.Clear();\n        this.Columns.Add(new SPBoundField { HeaderText = "Item", DataField = "YourObjectProperty" });\n\n        // Bind the data to the grid\n        this.DataSource = data;\n        this.DataBind();\n\n        // Render the grid\n        base.RenderContents(writer);\n    }\n\n    protected override void RenderRow(HtmlTextWriter writer, int rowNumber)\n    {\n        // Get the current item\n        YourObjectType item = (YourObjectType)this.Rows[rowNumber].DataItem;\n\n        // Render a custom cell with complex rendering\n        writer.RenderBeginTag(HtmlTextWriterTag.Td);\n        writer.Write(item.YourObjectProperty);\n        // Add other custom rendering here...\n        writer.RenderEndTag();\n    }\n}