using System.Web.Caching;\n\npublic class DatabaseCache\n{\n    public static void CacheQueryResults(string query, object results)\n    {\n        Cache cache = HttpRuntime.Cache;\n        cache.Insert(query, results, null, DateTime.Now.AddHours(1), TimeSpan.Zero);\n    }\n\n    public static object GetCachedQueryResults(string query)\n    {\n        Cache cache = HttpRuntime.Cache;\n        return cache[query];\n    }\n}