// Example of lazy-loading a type using the IoC container\npublic class MyService\n{\n    private readonly IMyInterface _myInterface;\n\n    public MyService(IMyInterface myInterface)\n    {\n        _myInterface = myInterface;\n    }\n}\n\n// Only load the type when it's actually needed\npublic class MyIoCContainer\n{\n    public IMyInterface GetMyInterface()\n    {\n        // Load the type and instantiate it only when needed\n        return new MyInterfaceImplementation();\n    }\n}