using Oracle.ManagedDataAccess.Client;\nusing System;\n\nclass OracleMonitor\n{\n    static void Main(string[] args)\n    {\n        // Connection string\n        string connectionString = "User Id=<username>;Password=<password>;Data Source=<hostname>:<port>/<servicename>;";\n\n        // SQL query to get last INSERT/UPDATE timestamp\n        string query = "SELECT MAX(TIMESTAMP) FROM SYS.DBA_TAB_MODIFICATIONS WHERE TABLE_OWNER = '<schema_name>'";\n\n        try\n        {\n            using (OracleConnection conn = new OracleConnection(connectionString))\n            {\n                conn.Open();\n\n                using (OracleCommand cmd = new OracleCommand(query, conn))\n                {\n                    using (OracleDataReader reader = cmd.ExecuteReader())\n                    {\n                        while (reader.Read())\n                        {\n                            DateTime lastModification = reader.GetDateTime(0);\n                            Console.WriteLine($"Last modification: {lastModification}");\n                        }\n                    }\n                }\n            }\n        }\n        catch (OracleException ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}