using System;\nusing System.Windows.Forms;\nusing WIA;\n\nclass CameraCapture\n{\n    [STAThread]\n    static void Main()\n    {\n        // Create a WIA manager\n        WIA.Manager manager = new WIA.Manager();\n\n        // Get the first camera device\n        WIA.Device camera = manager.Devices[0];\n\n        // Create a WIA command to take a picture\n        WIA.Command takePictureCmd = new WIA.Command(camera.Commands["Take Picture"]);\n\n        // Execute the command\n        WIA.ImageFile image = (WIA.ImageFile)takePictureCmd.Execute();\n\n        // Save the image to a file\n        image.SaveFile("captured_image.jpg");\n    }\n}