using System;\nusing System.Net.Http;\nusing System.Net.Http.Headers;\n\nnamespace SSISPackageName\n{\n    public class WebServiceClient\n    {\n        private readonly HttpClient _httpClient;\n\n        public WebServiceClient(string baseUrl)\n        {\n            _httpClient = new HttpClient { BaseAddress = new Uri(baseUrl) };\n            _httpClient.DefaultRequestHeaders.Accept.Add(new MediaTypeWithQualityHeaderValue("application/json"));\n            _httpClient.Timeout = TimeSpan.FromMinutes(10); // Increase timeout to 10 minutes\n        }\n\n        public HttpResponseMessage SendRequest(string requestUri)\n        {\n            return _httpClient.GetAsync(requestUri).Result;\n        }\n    }\n}