// Find the form you want to submit\nvar form = htmlDoc.DocumentNode.SelectSingleNode("//form[@id='myForm']");\n\n// Create a dictionary to store the form data\nvar formData = new Dictionary<string, string>();\n\n// Populate the form data\nforeach (var input in form.SelectNodes(".//input"))\n{\n    formData.Add(input.GetAttributeValue("name", ""), input.GetAttributeValue("value", ""));\n}\n\n// Send a POST request to the form URL\nresponse = await client.PostAsync(form.GetAttributeValue("action", ""), new FormUrlEncodedContent(formData));