using Dapper;\nusing System.Data.OleDb;\n\n// create an OleDb connection\nusing (var connection = new OleDbConnection("Provider=SQLOLEDB;Data Source=myServer;Initial Catalog=myDatabase;User ID=myUser;Password=myPassword;"))\n{\n    // create a Dapper command\n    var command = connection.CreateCommand();\n    command.CommandText = "myStoredProcedure";\n    command.CommandType = CommandType.StoredProcedure;\n\n    // add parameters\n    command.Parameters.AddWithValue("@param1", "value1");\n    command.Parameters.AddWithValue("@param2", "value2");\n    // ...\n\n    // execute the stored procedure\n    connection.Execute(command);\n}