using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\nusing System.Web;\n\npublic partial class ConcatenatePages : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string[] pageUrls = GetPageUrlsFromDatabase(); // implement this method to fetch page URLs from your database\n\n        StringBuilder concatenatedContent = new StringBuilder();\n\n        foreach (string pageUrl in pageUrls)\n        {\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(pageUrl);\n            HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n            using (Stream responseStream = response.GetResponseStream())\n            {\n                using (StreamReader reader = new StreamReader(responseStream, Encoding.UTF8))\n                {\n                    concatenatedContent.Append(reader.ReadToEnd());\n                }\n            }\n        }\n\n        Response.Write(concatenatedContent.ToString());\n    }\n}