using System.Xml;\n\npublic class iTunesXmlMonitor\n{\n    private string _iTunesXmlPath;\n    private XmlDocument _iTunesXmlDoc;\n\n    public iTunesXmlMonitor(string itunesXmlPath)\n    {\n        _iTunesXmlPath = itunesXmlPath;\n        _iTunesXmlDoc = new XmlDocument();\n    }\n\n    public void MonitorChanges()\n    {\n        while (true)\n        {\n            _iTunesXmlDoc.Load(_iTunesXmlPath);\n            XmlNode currentTrackNode = _iTunesXmlDoc.SelectSingleNode("//currentTrack");\n            if (currentTrackNode != null)\n            {\n                string currentTrackName = currentTrackNode.InnerText;\n                // Your track changed logic here\n                Debug.WriteLine("Track changed: " + currentTrackName);\n            }\n            Thread.Sleep(1000); // Poll every 1 second\n        }\n    }\n}