public class xValValidator : BaseValidator\n{\n    protected override bool EvaluateIsValid()\n    {\n        // Get the validation rules from the xVal configuration\n        var validationRules = xVal.Configuration.ConfigurationManager.GetValidationRules();\n\n        // Get the control to validate\n        var controlToValidate = GetControlToValidate();\n\n        // Perform the validation using xVal\n        var validator = new xVal.Validator(validationRules);\n        var validationResults = validator.Validate(controlToValidate);\n\n        // Return the validation result\n        return validationResults.IsValid;\n    }\n\n    protected override void OnPreRender(EventArgs e)\n    {\n        base.OnPreRender(e);\n\n        // Generate the client-side validation script\n        var script = xVal.ClientSideValidation.GetClientSideValidationScript(GetControlToValidate().ClientID);\n        Page.ClientScript.RegisterStartupScript(GetType(), "xValValidationScript", script, true);\n    }\n}