public partial class SelectorTool : UserControl\n{\n    public static DependencyProperty LeftItemsProperty = DependencyProperty.Register("LeftItems", typeof(ObservableCollection<ISelectable>), typeof(SelectorTool));\n    public ObservableCollection<ISelectable> LeftItems { get { return (ObservableCollection<ISelectable>)GetValue(LeftItemsProperty); } set { SetValue(LeftItemsProperty, value); } }\n\n    public static DependencyProperty RightItemsProperty = DependencyProperty.Register("RightItems", typeof(ObservableCollection<ISelectable>), typeof(SelectorTool));\n    public ObservableCollection<ISelectable> RightItems { get { return (ObservableCollection<ISelectable>)GetValue(RightItemsProperty); } set { SetValue(RightItemsProperty, value); } }\n\n    public SelectorTool()\n    {\n        InitializeComponent();\n        LeftItems = new ObservableCollection<ISelectable>();\n        RightItems = new ObservableCollection<ISelectable>();\n    }\n\n    private void LeftListBox_SelectionChanged(object sender, SelectionChangedEventArgs e)\n    {\n        if (LeftListBox.SelectedItem != null)\n        {\n            var item = LeftListBox.SelectedItem as ISelectable;\n            if (item != null)\n            {\n                LeftItems.Remove(item);\n                RightItems.Add(item);\n            }\n        }\n    }\n\n    private void RightListBox_SelectionChanged(object sender, SelectionChangedEventArgs e)\n    {\n        if (RightListBox.SelectedItem != null)\n        {\n            var item = RightListBox.SelectedItem as ISelectable;\n            if (item != null)\n            {\n                RightItems.Remove(item);\n                LeftItems.Add(item);\n            }\n        }\n    }\n}