using System.Text.RegularExpressions;\n\npublic class MarkdownConverter\n{\n    public static string ConvertMarkdownToHtml(string markdownText)\n    {\n        // Convert *italicized* text to <em>italicized</em>\n        markdownText = Regex.Replace(markdownText, @"\*(.*?)\*", "<em>$1</em>");\n\n        // Convert **bold** text to <strong>bold</strong>\n        markdownText = Regex.Replace(markdownText, @"\*\*(.*?)\*\*", "<strong>$1</strong>");\n\n        return markdownText;\n    }\n}