using System;\nusing System.IO;\n\nclass DownloadHandler\n{\n    static void Main(string[] args)\n    {\n        // Get the file path and name\n        string filePath = args[0];\n\n        // Prompt the user\n        Console.WriteLine("You are about to download a file of type 'application/myf'. Are you sure?");\n        Console.WriteLine("File path: " + filePath);\n        Console.Write("Confirm (y/n): ");\n        string confirm = Console.ReadLine();\n\n        if (confirm.ToLower() == "y")\n        {\n            // Save the file to the user's downloads folder\n            string downloadsFolder = Environment.GetFolderPath(Environment.SpecialFolder.UserProfile) + "\\Downloads";\n            string fileName = Path.GetFileName(filePath);\n            string destPath = Path.Combine(downloadsFolder, fileName);\n            File.Copy(filePath, destPath);\n\n            Console.WriteLine("File saved to " + destPath);\n        }\n        else\n        {\n            Console.WriteLine("Download cancelled.");\n        }\n    }\n}