using (Image image = Image.FromFile("C:\\source.jpg"))\n{\n    ImageFormat sourceFormat = image.RawFormat;\n    EncoderParameters encoderParams = null;\n\n    try\n    {\n        if (sourceFormat.Guid == ImageFormat.Jpeg.Guid)\n        {\n            encoderParams = new EncoderParameters(1);\n            encoderParams.Param[0] = new EncoderParameter(Encoder.Transformation, (long)EncoderValue.TransformRotate90);\n        }\n\n        ImageCodecInfo encoder = GetEncoder(sourceFormat);\n        image.Save("C:\\target.jpg", encoder, encoderParams);\n    }\n    finally\n    {\n        if (encoderParams != null)\n            encoderParams.Dispose();\n    }\n}\n\nprivate ImageCodecInfo GetEncoder(ImageFormat format)\n{\n    foreach (var info in ImageCodecInfo.GetImageEncoders())\n        if (info.FormatID == format.Guid)\n            return info;\n    return null;\n}