public partial class MainWindow : Window\n{\n    public MainWindow()\n    {\n        InitializeComponent();\n        this.PreviewKeyUp += new KeyEventHandler(MainWindow_PreviewKeyUp);\n    }\n\n    void MainWindow_PreviewKeyUp(object sender, KeyEventArgs e)\n    {\n        if (e.SystemKey == Key.LeftAlt || e.SystemKey == Key.RightAlt)\n        {\n            MainMenu.Visibility = MainMenu.Visibility == Visibility.Visible ? Visibility.Collapsed : Visibility.Visible;\n        }\n    }\n}