void MoveUpButton_Click(object sender, EventArgs e)\n{\n    int selectedIndex = dataGrid.SelectedIndex;\n    if (selectedIndex > 0)\n    {\n        T item = bindingList[selectedIndex];\n        bindingList.RemoveAt(selectedIndex);\n        bindingList.Insert(selectedIndex - 1, item);\n        bindingList.OnListChanged(new ListChangedEventArgs(ListChangedType.ItemMoved, selectedIndex, selectedIndex - 1));\n    }\n}\n\nvoid MoveDownButton_Click(object sender, EventArgs e)\n{\n    int selectedIndex = dataGrid.SelectedIndex;\n    if (selectedIndex < bindingList.Count - 1)\n    {\n        T item = bindingList[selectedIndex];\n        bindingList.RemoveAt(selectedIndex);\n        bindingList.Insert(selectedIndex + 1, item);\n        bindingList.OnListChanged(new ListChangedEventArgs(ListChangedType.ItemMoved, selectedIndex, selectedIndex + 1));\n    }\n}